/* loop through all the global variables to set local globals as per the repo settings */
/* this can be removed once POSTMAN proper handles syncing of environment variables */

tests['globals fetched'] = responseCode.code === 200;

if (responseCode.code === 200) {
    var data = JSON.parse(responseBody);

    // if responseBody parses correctly we should have a javascript array
    if (typeof data === "object" && data.length > 0) {
        for(var item of data) {
            postman.setGlobalVariable(item.key, item.value);
        }

        tests['global variables set successfully'] = true;  
    } else {
        tests['global variables set successfully'] = false;
    }
}

postman.setGlobalVariable("module:describe-it", responseBody);
try {
    eval(responseBody);
}catch (e) { }

tests['Framework ready'] = (typeof describe === 'function');

/* loop through all the snc1-uat variables to set local environment as per the repo settings */
/* this can be removed once POSTMAN proper handles syncing of environment variables */

tests[postman.getEnvironmentVariable("postman:environment") + ' environment variables fetched'] = responseCode.code === 200;

if (responseCode.code === 200) {
    var data = JSON.parse(responseBody);

    // if responseBody parses correctly we should have a javascript array
    if (typeof data === "object" && data.values.length > 0) {
        for(var item of data.values) {
            if (item.key !== "consumer_id" && item.key !== "access_token") {
                postman.setEnvironmentVariable(item.key, item.value);
            }
        }

        tests['global variables set successfully'] = true;  
    } else {
        tests['global variables set successfully'] = false;
    }
}
